var mQuery=window.matchMedia("(min-width: 1200px)"),togleSelector=document.querySelectorAll(".field--multirow");if(window.innerWidth>1200)for(let e=0;e<=togleSelector.length-1;e++)togleSelector[e].classList.remove("field--multirow");function toggleClass(e){if(e.matches)for(let e=0;e<=togleSelector.length-1;e++)togleSelector[e].classList.remove("field--multirow");else for(let e=0;e<=togleSelector.length-1;e++)togleSelector[e].classList.add("field--multirow")}mQuery.addListener(toggleClass);